rn $("#error").html(msg_email_blank);                                    if (email.match(/([\w-\.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([\w-]+\.)+))([a-zA-Z]{2,4}|[0-9]{1,3})(\]?)/) == null)                                        return $("#error").html(email_invalid);                                    $.get("user.php?act=check_email", {email: email, mask: Math.random()}, function(data) {                                        if (data == "false")                                            return $("#error").html(msg_email_registered);                                        else                                            checkFlag.email = 1;                                    });                                    break;                                case "password":                                    checkFlag.password = 0;                                    var password = $("#signup_password").val();                                    if (password.length < 6)                                        return $("#error").html(password_shorter);                                    checkFlag.password = 1;                                    break;                                case "password2":                                    checkFlag.password2 = 0;                                    var password = $("#signup_password").val();                                    var password2 = $("#signup_password2").val();                                    if (password != password2)                                        return $("#error").html(confirm_password_invalid);                                    checkFlag.password2 = 1;                                    break;                                case "howto":                                    checkFlag.howto = 0;                                    var howto = $("#signup_howto").val();                                    if (howto == "")                                        return $("#error").html(reg_source_unselected);                                    checkFlag.howto = 1;                                    break;                                case "valicode":                                    checkFlag.valicode = 0;                                    var valicode = $("#signup_valicode").val();                                    if (valicode == "")                                        return $("#error").html(identifying_code_is_needed);                                    $.get("user.php?act=check_valicode", {valicode: valicode, mask: Math.random()}, function(data) {                                        if (data == "false")                                            return $("#error").html(invalid_captcha);                                        else                                            checkFlag.valicode = 1;                                    });                                    break;                                case "agree":                                    checkFlag.agree = 0;                                    var agree = $("#signup_agree").attr("checked");                                    if (agree == false)                                        return $("#error").html(agreement);                                    checkFlag.agree = 1;                                    break;                            }                            $("#error").html("&nbsp;");                            return "isOK";                        }                        function register() {                            for (index in checkFlag) {                                if (checkFlag[index] == 0) {                                    if (check(index) != "isOK") {                                        return false;                                    }                                }                            }                            setTimeout(doSubmit_signup_form, 0);                        }                        function doSubmit_signup_form() {                            $("#signup_form").submit();                        }                    </script>                </div>            </div>        </div>        <div id="footer" class="simple">            <div id="footer_copy">                “TS” AND “Three Seasons” ARE REGISTERED TRADEMARKS BELONG TO LIGHTINTHEBOX, INC.<br/>                © 2009-2011 - ONLYTS ALL RIGHTS RESERVED. <a href="http://www.miibeian.gov.cn/" target="_blank" rel="nofollow">沪ICP备10211500号</a>            </div>        </div>        <!--[if IE 6]>                <script type="text/javascript" >                        DD_belatedPNG.fix('.tag_c1,.tag_c2,.tag_c3,.tag_c4,.cbottom,#tsTopAdvClose');                </script>                <![endif]-->        <div class="hide_element"><script type="text/javascript">    var _gaq = _gaq || [];    _gaq.push(['_setAccount', 'UA-20788741-2']);    _gaq.push(['_setDomainName', 'onlyts.cn']);    _gaq.push(['_trackPageview']);    (function() {        var ga = document.createElement('script');        ga.type = 'text/javascript';        ga.async = true;        ga.src = ('https:' == document.location.protocol ? 'https://ssl' : 'http://www') + '.google-analytics.com/ga.js';        var s = document.getElementsByTagName('script')[0];        s.parentNode.insertBefore(ga, s);    })();            </script>            <script type="text/javascript">                var _bdhmProtocol = (("https:" == document.location.protocol) ? " https://" : " http://");                document.write(unescape("%3Cscript src='" + _bdhmProtocol + "hm.baidu.com/h.js%3F609ce6669ca441ca061d684ca80aa054' type='text/javascript'%3E%3C/script%3E"));            </script>            <script language="javascript" type="text/javascript" src=" /Home/public/js/